package tnsif;

public class program4Operators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=5;
		int b=6;
		int add=a+b;
		System.out.println(add);
		int sub=a-b;
		System.out.println(sub);
		int mul=a*b;
		System.out.println(mul);
		int mod=a%b;//reminder
		System.out.println(mod);
		int num=5;
		                          //uinary operator instead of num+1
		System.out.println(num++);//5 after it increments 
		System.out.println(num);//6
		System.out.println(++num);//7 before only it increments
		System.out.println(num);//7
		

	}

}
